$(function () {
  "use strict";
  var pro = 14500; 
  var business = 24500; 
  var n = $("#priceSwitch"),
    e = $(".pricing-standard-value"),
    i = $(".pricing-enterprise-value"),
    a = $(".enterprise-pricing .annual-pricing"),
    r = $(".standard-pricing .annual-pricing");
  n.on("change", function () {
    if ($(this).is(":checked")) {
      i.html(business), a.removeClass("d-none").html("₦ "+ business * 12 +" / year");
      e.html(pro), r.removeClass("d-none").html("₦ "+ pro * 12 +" / year");
    } else i.html(business), a.addClass("d-none"), e.html(pro), r.addClass("d-none");
  });
});
